package tp.v3.tests;import tp.v3.Liste;import tp.v3.ListeMutable;public class TestListes {     public static void main(String[] args){         Liste<String> liste = Liste.cons("item1",Liste.vide());         System.out.println(liste.representer());         liste = Liste.cons("item2",liste);         System.out.println(liste.representer());         System.out.println("tete: "+liste.tete());         System.out.println("miroir: "+liste.miroir().representer());          ListeMutable<String> listeMutable = ListeMutable.cons("alter1", ListeMutable.vide());         System.out.println(listeMutable.representer());         listeMutable = ListeMutable.cons("alter2", listeMutable);         System.out.println(listeMutable.representer());         System.out.println("tete: "+listeMutable.tete());         System.out.println("miroir: "+listeMutable.miroir().representer());     } } 